
public class J230212_05_VarEx1 {
//ch2-3 변수란? 변수의 선언과 저장
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("6 + 3");//큰따옴표 안에 있는 내용들은 그대로 출력되기 때문에
		System.out.println(6 + 3); //이러면 9나온다
		int x = 4, y = 2;
		System.out.println(x + y);
		System.out.println(x - y);
		System.out.println(x * y);
		System.out.println(x / y);
	}

}

