
public class J230212_06 {
	// ch2-4 변수의 타입
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//p.50쪽
		int x = 100;
		double pi = 3.14; //double은 15자리까지 표현, 실수를 저장하기 위한 타입
		char ch = 'a'; //char 문자를 저장하기 위한 타입
		String str = "abc"; //String은 여러문자(문자열, String) 저장학 위한 타입
		
		System.out.println(x);
		System.out.println(pi);
		System.out.println(ch);
		System.out.println(str);
	}
	

}
